package Session_3;

public class WhileEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;   //variable initialize
		while(a<10) { //a is become 10 terminate the loop
			System.out.println("the value are:"+a);
			a++; //increment
		}
		System.out.println("the final value of a:"+a); //10
	}

}
